import React, {useRef} from "react";
import PropTypes from "prop-types";
import withStyles from '../../../util/styleComponentUtil/withStyles';
import styles, { StyledSearch } from './Search.style';



const Search = ({className}) => {
   const searchInput = useRef();

  const searchAnimation = () => {
    console.log(searchInput.current.classList)
    // searchBtn.classList.toggle("close");
    // input.classList.toggle("square");
  }

  return (
    <StyledSearch className={`${className}`}>
      <form id="content">
        <input type="text" name="input" class="input" id="search-input" ref={searchInput} />
        <button type="reset" class="search" id="search-btn" onClick={searchAnimation}></button>
      </form>
    </StyledSearch>
  )
};

Search.propTypes = {};

Search.defaultProps = {};

export default withStyles(Search, styles);

export { Search as SearchVanilla };
