import styled, { css } from "styled-components";

export default css``;

export const StyledStory = styled.section`{
  border-radius: 5px;
  box-shadow: 0 2px 30px rgba(0, 0, 0, 0.2);
  transition: 0.3s;
  padding: 5px;
  margin-bottom: 15px;
  color: ${({ theme }) => theme.highLight};

  &:hover {
    transform: translateY(-10px);
    transition: 0.20s;
  }

  .heading {
    color: ${({ theme }) => theme.highLight};
  }
}`
