import React, { useState, useEffect } from 'react';
import { MAX_STORIES, INITIAL_STORY_LOAD } from './config';


/**
 * custom hook
 * (innerHeight + scrollTop) != offsetHeight 
 */
const useInfiniteScroll = () => {
  const [loading, setLoading] = useState(false);
  const [count, seCount] = useState(INITIAL_STORY_LOAD);

  const handleScroll = () => {
    if(window.innerHeigh + document.documentElement.scrollTop !== document.documentElement.offsetHeight || loading) {
      return false;
    }

    setLoading(true);
  }


  useEffect(() => {
    if (!loading) return;

    if (count + INITIAL_STORY_LOAD >= MAX_STORIES) {
      setCount(MAX_STORIES)
    } else {
      setCount(count + INITIAL_STORY_LOAD)
    }

    setLoading(false)
  }, [loading]);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll)
  }, []);


  return { count }
}
