import styled, { css } from "styled-components";

export default css``;

export const StyledHeading = styled.header`{
  position: relative;

  h1 {
    color: ${({ theme }) => theme.highLight};
  }
  &:after {
    content: '';
    position: absolute;
    width: 100%;

    border-bottom: 2px solid ${({ theme }) => theme.highLight};
  }
}`
