import styled, { css } from "styled-components";

export default css``;

export const StyledStory = styled.section`{
  border-radius: 5px;
  box-shadow: 0 2px 30px rgba(0, 0, 0, 0.2);
  transition: 0.3s;
  padding: 15px;
  margin-bottom: 25px;
  color: ${({ theme }) => theme.highLight};
  position: relative;

  &:after {
    content: '';
    position: absolute;
    border-bottom: 1px solid ${({ theme }) => theme.highLight};
    width: 100vw;
    left: 50%;
    right: 50%;
    margin-left: -50vw;
    margin-right: -50vw;
    top: 50%;
  }

  &:hover {
    transform: translateY(-10px);
    transition: 0.20s;
  }

  .heading {
    color: ${({ theme }) => theme.highLight};
    font-family: ${props => props.theme.fontFamilyRegular};
    font-size: 20px;
    margin-bottom: 0;
  }

  .news-type {
    color: ${({ theme }) => theme.text};
    font-family: ${props => props.theme.fontFamilyRegular};
  }

  .submitted-name {
    text-align: end;
    color: ${({ theme }) => theme.text};
    font-family: ${props => props.theme.fontFamilyRegular};
    span {
      color: ${({ theme }) => theme.highLight};
    }
  }
}`
